var treeData = {};
var matchedNodes = [];
var lastQuery = '';
/*
	name <String>
		Default type: 'MouseArea(0x12345678)'
		Default type + objectName: 'MouseArea(0x12345678, "Clicker")'
		Custom type: 'Hecataeus_QMLTYPE_56_QML_87(0x12345678)'
		Custom type + objectName: 'Hecataeus_QMLTYPE_56_QML_87(0x12345678, "RootAppWindow")'
*/
function getNodeData(name) {
	var parts = name.split('(');
	var type = parts[0].split('_')[0];
	var objectName = parts[1].split('"');
	objectName = objectName.length === 3
		? objectName[1]
		: "";
	return {
		type: type,
		objectName: objectName
	};
}


// WebSocket
var ws = new WebSocket('ws://localhost:12345');
ws.onmessage = function(event) {
	var data = JSON.parse(event.data);
	console.log(data);

	switch (data.method) {
		case 'listElements':
			updateTree(data.result);
			break;
		case 'listProperties':
			if (data.result instanceof Array && data.result.length) {
				updateProperties(data.result);
			}
			break;
		default:
			console.warn('There is no handler for ', data.method)
			return;
	}
};
ws.onerror = function() {
	console.error(event);
};
ws.onopen = function() {
	// base = '{"jsonrpc": "2.0", "method": "setProperty", "params": ["SandwichBackButton", "color", "blue"]}'
	listElements();
};


function listElements() {
	var request = {
		jsonrpc: '2.0',
		method: 'listElements'
	};
	ws.send(JSON.stringify(request));
}
function listProperties(selector, index) {
	var request = {
		jsonrpc: '2.0',
		method: 'listProperties',
		params: [selector, index]
	};
	ws.send(JSON.stringify(request));
}
function getProperty(selector, index, name) {
	var request = {
		jsonrpc: '2.0',
		method: 'getProperty',
		params: [selector, index, name]
	};
	ws.send(JSON.stringify(request));
}


// Command Line
var input = document.querySelectorAll('.input')[0];
input.addEventListener('keyup', handleInput);
input.addEventListener('change', handleInput);
// example: "DirectoryPage[2]"
var selectorRegexp = /([^\[\]]+)(\[([0-9]+)\])?/;
function handleInput(event) {
	var query = event.target.value;
	if (!query) return;
	var queryParts = query.split('.');
	if (queryParts.length === 1) {
		var q = queryParts[0];
		var r = selectorRegexp.exec(q);
		var i = r[3];
		q = r[1];

		var result = findNodes(q.toLowerCase());
		if (result) {
			if (result.nodes.length === 1) {
				listProperties(getSelector(result.nodes[0], result.searchType), 0);
			} else if (result.nodes.length > 1 && i != null) {
				listProperties(getSelector(result.nodes[i], result.searchType), i);
			}
		}
	}
}
function getSelector(node, searchType) {
	return searchType === 'objectName'
		? '#' + node.objectName
		: node.type;
}


function findNodes(query) {
	if (query === lastQuery
		|| (query.startsWith('#') && query.length === 1)) {

		return;
	}

	var byObjectName = query.startsWith('#');
	var result = {
		searchType: byObjectName
			? 'objectName'
			: 'type',
		nodes: byObjectName
			? findByObjectName(treeData, query.substr(1))
			: findByType(treeData, query)
	};
	if (!result.nodes.length) {
		unmatchElements(matchedNodes);
	}
	matchedNodes = query ? result.nodes : [];
	updateResults(matchedNodes);
	lastQuery = query;
	return result;
}
function findByType(node, query, results) {
	results = results || [];
	if (node.type.toLowerCase().startsWith(query)) {
		results.push(node);
		matchType(node.element, node.type, query);
	} else if (matchedNodes.indexOf(node) !== -1) {
		unmatchType(node.element, node.type);
	}
	if (node.children) {
		node.children.forEach(function(child) {
			findByType(child, query, results);
		});
	}
	return results;
}
function findByObjectName(node, query, results) {
	results = results || [];
	if (node.objectName.toLowerCase().startsWith(query)) {
		results.push(node);
		matchObjectName(node.element, node.objectName, query);
	} else if (matchedNodes.indexOf(node) !== -1) {
		unmatchObjectName(node.element, node.objectName);
	}
	if (node.children) {
		node.children.forEach(function(child) {
			findByObjectName(child, query, results);
		});
	}
	return results;
}


function updateTree(elements) {
	treeData = elements;
	createNode(treeElement, treeData);
}
function checkResults(visualType, nodeData) {
	var selector = '';
	var result = [];

	if (visualType === 'result') {
		if (nodeData.objectName) {
			if (matchedNodes.length) {
				var result = matchedNodes.filter(function(matchedNode) {
					return matchedNode.objectName === nodeData.objectName;
				});
				if (result.length === 1) {
					selector = '#' + nodeData.objectName;
				} else {
					selector = '#' + nodeData.objectName + '[' + result.indexOf(nodeData) + ']';
				}
			}
		} else {
			if (matchedNodes.length) {
				var result = matchedNodes.filter(function(matchedNode) {
					return matchedNode.type === nodeData.type;
				});
				if (result.length === 1) {
					selector = nodeData.type;
				} else {
					selector = nodeData.type + '[' + result.indexOf(nodeData) + ']';
				}
			}
		}
	} else if (visualType === 'tree-node') {
		selector = nodeData.objectName
			? '#' + nodeData.objectName
			: nodeData.type;
	}

	input.value = selector;
	input.dispatchEvent(new Event('change'));

	return result;
}


// DOM
var treeElement = document.querySelectorAll('.tree')[0];
var resultsElement = document.querySelectorAll('.results')[0];
var resultsCountElement = document.querySelectorAll('.results-count-number')[0];
var propertiesElement = document.querySelectorAll('.properties')[0];
var propertiesCountElement = document.querySelectorAll('.properties-count-number')[0];


/*
	nodes {
		name: <String>,
		children: [<Node>]
	}
*/
function createNode(parent, node, deepness) {
	deepness = deepness || 0;

	var fragment = document.createDocumentFragment();
	node.element = createNodeElement(node, 'tree-node', deepness);
	fragment.appendChild(node.element);

	if (node.children) {
		node.children.forEach(function(child) {
			createNode(fragment, child, deepness + 4);
		});
	}

	parent.appendChild(fragment);
}
/*
	nodeData {
		type: <String>,
		[objectName: <String>]
	}
	visualType <String> ('tree-node'|'result')
	deepness <Number>
*/
function createNodeElement(nodeData, visualType, deepness) {
	var node = document.createElement('div');
	addClass(node, visualType);

	var space = document.createElement('span');
	addClass(space, 'space');
	space.innerText = new Array(deepness * 4).join(' ');
	node.appendChild(space);

	var type = document.createElement('span');
	addClass(type, 'type');
	type.innerText = nodeData.type;
	node.appendChild(type);

	if (nodeData.objectName) {
		var objectName = document.createElement('span');
		addClass(objectName, 'objectName');
		objectName.innerText = nodeData.objectName;
		node.appendChild(objectName);
	}

	node.addEventListener('click', function(event) {
		var results = checkResults(visualType, nodeData);
	});

	return node;
}
function updateResults(nodes) {
	var fragment = document.createDocumentFragment();
	nodes.forEach(function(node) {
		fragment.appendChild(createNodeElement(node, 'result', 0));
	});
	resultsElement.innerHTML = '';
	resultsElement.appendChild(fragment);

	resultsCountElement.innerText = nodes.length;
}
function updateProperties(properties) {
	var fragment = document.createDocumentFragment();
	var filteredProperties = properties.filter(function(prop) {
		return !prop.endsWith('Changed');
	});
	filteredProperties.forEach(function(prop) {
		var propElement = document.createElement('div');
		addClass(propElement, 'property');
		propElement.innerText = prop;
		fragment.appendChild(propElement);
	});
	propertiesElement.innerHTML = '';
	propertiesElement.appendChild(fragment);

	propertiesCountElement.innerText = properties.length;
}
function addClass(element, className) {
	var classes = element.className.split(' ');
	if (!(className in classes)) {
		classes.push(className);
		element.className = classes.join(' ');
	}
}
function removeClass(element, className) {
	var classes = element.className.split(' ');
	var index = classes.indexOf(className);
	if (index !== -1) {
		classes.splice(index, 1);
		element.className = classes.join(' ');
	}
}
/*
	element <div .node>
				<span .space>
				<span .type>
				[<span .objectName>]
	string <String>
	query <String>
*/
function matchType(element, string, query) {
	var typeElement = element.children[1];
	typeElement.innerHTML = '<span class="match">' + string.substr(0, query.length) + '</span>' + string.substr(query.length);
}
function unmatchType(element, string) {
	var typeElement = element.children[1];
	typeElement.innerHTML = string;
}
function matchObjectName(element, string, query) {
	var typeElement = element.children[2];
	typeElement.innerHTML = '<span class="match">' + string.substr(0, query.length) + '</span>' + string.substr(query.length);
}
function unmatchObjectName(element, string) {
	var typeElement = element.children[2];
	typeElement.innerHTML = string;
}
function unmatchElements(nodes) {
	nodes.forEach(function(node) {
		node.element.children[1].innerText = node.type;
		if (node.objectName) {
			node.element.children[2].innerText = node.objectName;
		}
	});
}

